                [254, '', '', 'https://fitnessprotravel.com/files/97241_NOAPC.jpg', '', 'David Alfaro Siqueiros No. 164, Col. Zona Hotelera Las Glorias', 'Puerto Vallarta, Jalisco', 80, '', 592, 'Gigante', 'Juan', '+52 (322) 226-2900', 'erica@fitbodiesinc.com', '', '', '&lt;div class=&quot;marginT&quot; style=&quot;font-weight: bold;&quot;&gt;Puerto Vallarta, Mexico Transfers&lt;/div&gt;
